import { DECREMENT, INCREASE, INCREMENT, TOGGLE } from "../store";
import classes from "./Counter.module.css";
import { useSelector, useDispatch } from "react-redux";

const Counter = () => {
  // 使用 useSelector 时，会自动 subscription to redux store
  const counter = useSelector((state) => state.counter); //从 store 里提取 state
  const show = useSelector((state) => state.showCounter);

  const dispatch = useDispatch();

  // 调用 store 里对应的 action 改变 counter 的 state
  const incrementHandler = () => {
    dispatch({ type: INCREMENT });
  };

  const increaseHandler = () => {
    dispatch({ type: INCREASE, amount: 5 }); //放到 action 动态的变化
  };

  const decrementHandler = () => {
    dispatch({ type: DECREMENT });
  };

  const toggleCounterHandler = () => {
    dispatch({ type: TOGGLE }); //点击显示或隐藏 DOM 元素
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      {show && <div className={classes.value}>{counter}</div>}
      <div>
        <button onClick={incrementHandler}>Increment</button>
        <button onClick={increaseHandler}>Increment by 5</button>
        <button onClick={decrementHandler}>Decrement</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};
export default Counter;

